package com.qhc.steigenberger.domain;import java.io.Serializable;import java.util.Date;import com.fasterxml.jackson.annotation.JsonFormat;/** *  * Function: Data transfer object. <br>  * * @author walker */public class BillingPlan		implements Serializable {	private static final long serialVersionUID = 2857860229975962803L;	/* Id */	private Integer id = null;	/* 订单详情ID */	private Integer orderInfoId = null;	/* 回款条款code */	private String code = null;	/* 回款起始日期 */	@JsonFormat(shape = JsonFormat.Shape.STRING, pattern="yyyy-MM-dd",timezone = "GMT+8")	private Date payDate = null;	/* 回款比例 */	private Float percentage = null;	/* 预算回款原币金额 */	private Double amount = null;	/* 预算回款金额 */	private Double rmbAmount = null;	/* 备注 */	private String reason = null;	public Integer getId() {		return id;	}	public void setId(Integer id) {		this.id = id;	}	public Integer getOrderInfoId() {		return orderInfoId;	}	public void setOrderInfoId(Integer orderInfoId) {		this.orderInfoId = orderInfoId;	}	public String getCode() {		return code;	}	public void setCode(String code) {		this.code = code;	}	public Date getPayDate() {		return payDate;	}	public void setPayDate(Date payDate) {		this.payDate = payDate;	}	public Float getPercentage() {		return percentage;	}	public void setPercentage(Float percentage) {		this.percentage = percentage;	}	public Double getAmount() {		return amount;	}	public void setAmount(Double amount) {		this.amount = amount;	}	public Double getRmbAmount() {		return rmbAmount;	}	public void setRmbAmount(Double rmbAmount) {		this.rmbAmount = rmbAmount;	}	public String getReason() {		return reason;	}	public void setReason(String reason) {		this.reason = reason;	}	@Override	public int hashCode() {		final int prime = 31;		int result = 1;		result = prime * result + ((id == null) ? 0 : id.hashCode());		return result;	}	@Override	public boolean equals(Object obj) {		if (this == obj)			return true;		if (obj == null)			return false;		if (getClass() != obj.getClass())			return false;		BillingPlan other = (BillingPlan) obj;		if (id == null) {			if (other.id != null)				return false;		} else if (!id.equals(other.id))			return false;		return true;	}	@Override	public String toString() {		return "BillingPlan [id=" + id + ", orderInfoId=" + orderInfoId + ", code=" + code + ", payDate=" + payDate				+ ", percentage=" + percentage + ", amount=" + amount + ", rmbAmount=" + rmbAmount + ", reason="				+ reason + "]";	}}