package com.qhc.steigenberger.domain.form;import java.io.Serializable;import java.util.Date;import org.springframework.format.annotation.DateTimeFormat;import com.fasterxml.jackson.annotation.JsonFormat;/** *  * Function: Data transfer object. <br>  * * @author walker */public class BillingPlan		implements Serializable {	private static final long serialVersionUID = 2857860229975962803L;		/* Id */	private Integer id = null;	/* Code */	private String code = null;	/* Name */	private String name = null;	/* Amount */	private Double amount = null;	/* PayDate */	@DateTimeFormat(pattern = "yyyy-MM-dd")	@JsonFormat(shape = JsonFormat.Shape.STRING, pattern="yyyy-MM-dd",timezone = "GMT+8")	private Date payDate = null;	/* Reason */	private String reason = null;	/* OrderInfoId */	private String orderInfoId = null;	public BillingPlan(){	}	public Integer getId() {		return this.id;	}	public void setId(Integer id) {		this.id = id;	}	 	public String getCode() {		return this.code;	}	public void setCode(String code) {		this.code = code;	}	 	public String getName() {		return this.name;	}	public void setName(String name) {		this.name = name;	}	 	public Double getAmount() {		return this.amount;	}	public void setAmount(Double amount) {		this.amount = amount;	}	 	public Date getPayDate() {		return this.payDate;	}	public void setPayDate(Date payDate) {		this.payDate = payDate;	}	 	public String getReason() {		return this.reason;	}	public void setReason(String reason) {		this.reason = reason;	}	 	public String getOrderInfoId() {		return this.orderInfoId;	}	public void setOrderInfoId(String orderInfoId) {		this.orderInfoId = orderInfoId;	}	 	@Override	public int hashCode() {		final int prime = 31;		int result = 1;		result = prime * result + ((id == null) ? 0 : id.hashCode());		return result;	}	@Override	public boolean equals(Object obj) {		if (this == obj) {			return true;		}		if (obj == null) {			return false;		}		if (getClass() != obj.getClass()) {			return false;		}		final BillingPlan other = (BillingPlan) obj;		return (this.id == null ? other.id == null : this.id.equals(other.id));	}		public String toString() {	    final String tab = "  ";	    String str = "";	    str = "BillingPlan ( "	        + "id = " + this.id + tab	        + "code = " + this.code + tab	        + "name = " + this.name + tab	        + "amount = " + this.amount + tab	        + "payDate = " + this.payDate + tab	        + "reason = " + this.reason + tab	        + "orderInfoId = " + this.orderInfoId + tab	        + " )";		    return str;	}}